import React, { useState } from 'react';

const F1Button = ({ children, link, onClick }) => {
  const [isHover, setHover] = useState(false);

  const buttonStyle = {
    display: 'inline-block',
    backgroundColor: isHover ? '#00cbbd' : '#00f0dc',
    color: '#000',
    fontWeight: 900,
    fontSize: 20,
    padding: '14px 28px',
    textAlign: 'center',
    textTransform: 'uppercase',
    border: 'none',
    cursor: 'pointer',
    position: 'relative',
    margin: '16px auto',
    width: '80%',
    maxWidth: '400px',
    transition: 'all 0.3s ease',
    clipPath: 'polygon(5% 0%, 100% 0%, 100% 85%, 97% 100%, 0% 100%)',
    borderBottomRightRadius: 30,
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
    transform: isHover ? 'translateY(-2px)' : 'translateY(0)',
  };

  return (
    <button
      onClick={onClick}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      style={buttonStyle}
    >
      {children}
    </button>
  );
};

const App = () => {
  const [showOverlay, setShowOverlay] = useState(false);
  const [destination, setDestination] = useState('');

  const handleLaunch = (link) => {
    setShowOverlay(true);
    setDestination(link);
    setTimeout(() => {
      window.location.href = link;
    }, 2000);
  };

  return (
    <>
      {/* Overlay Animation */}
      {showOverlay && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100vw',
            height: '100vh',
            backgroundColor: '#000',
            color: '#00f0dc',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '3rem',
            fontWeight: '900',
            letterSpacing: '2px',
            zIndex: 9999,
            animation: 'flashEngage 2s ease-in-out',
          }}
        >
          ENGAGE ðŸš¦
        </div>
      )}

      {/* Main Content */}
      <div
        style={{
          position: 'absolute',
          top: 'env(safe-area-inset-top, 0)',
          left: 0,
          right: 0,
          bottom: 'env(safe-area-inset-bottom, 0)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px',
          backgroundImage: 'url("/ntu_f1_car_image.png")',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundColor: '#000',
          overflow: 'hidden',
          textAlign: 'center',
        }}
      >
        <img
          src="/image_1.png"
          alt="NTU F1 Logo"
          style={{
            width: 120,
            height: 120,
            borderRadius: '50%',
            objectFit: 'cover',
            boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
            marginBottom: 20,
          }}
        />
        <F1Button link="#" onClick={() => handleLaunch('https://example.com/events')}>Events</F1Button>
        <F1Button link="#" onClick={() => handleLaunch('https://instagram.com/yourhandle')}>Instagram</F1Button>
        <F1Button link="#" onClick={() => handleLaunch('https://example.com/join')}>Become a Member</F1Button>
        <F1Button link="#" onClick={() => handleLaunch('https://tiktok.com/@yourprofile')}>TikTok</F1Button>
      </div>

      {/* Keyframe animation injected */}
      <style>
        {`
          @keyframes flashEngage {
            0% { opacity: 0; transform: scale(0.9); }
            25% { opacity: 1; }
            50% { opacity: 0.6; }
            75% { opacity: 1; }
            100% { opacity: 1; transform: scale(1); }
          }
        `}
      </style>
    </>
  );
};

export default App;
