import React, { useState } from 'react';

const F1Button = ({ children, link }) => {
  const [isHover, setHover] = useState(false);
  const [isAnimating, setAnimating] = useState(false);

  const handleClick = () => {
    if (isAnimating) return; // prevent spam
    setAnimating(true);

    setTimeout(() => {
      window.location.href = link;
    }, 2000); // 2-second delay
  };

  const buttonStyle = {
    display: 'inline-block',
    backgroundColor: isHover ? '#00cbbd' : '#00f0dc',
    color: '#000',
    fontWeight: 900,
    fontSize: 20,
    padding: '14px 28px',
    textAlign: 'center',
    textTransform: 'uppercase',
    border: 'none',
    cursor: 'pointer',
    position: 'relative',
    margin: '16px auto',
    width: '80%',
    maxWidth: '400px',
    transition: 'all 0.3s ease',
    clipPath: 'polygon(5% 0%, 100% 0%, 100% 85%, 97% 100%, 0% 100%)',
    borderBottomRightRadius: 30,
    boxShadow: isAnimating
      ? '0 0 30px rgba(0, 229, 255, 0.8)'
      : '0 4px 15px rgba(0, 0, 0, 0.3)',
    transform: isAnimating
      ? 'scale(1.05)'
      : isHover
      ? 'translateY(-2px)'
      : 'translateY(0)',
  };

  return (
    <button
      onClick={handleClick}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      style={buttonStyle}
    >
      {isAnimating ? 'Loading...' : children}
    </button>
  );
};


const App = () => {
  return (
<div
  style={{
    position: 'absolute',
    top: 'env(safe-area-inset-top, 0)',
    left: 0,
    right: 0,
    bottom: 'env(safe-area-inset-bottom, 0)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px',
    backgroundImage: 'url("/ntu_f1_car_image.png")',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundColor: '#000', // fallback if image doesn't load
    overflow: 'hidden',
    textAlign: 'center',
  }}
>

      <img
        src="/image_1.png"
        alt="NTU F1 Logo"
        style={{
          width: 120,
          height: 120,
          borderRadius: '50%',
          objectFit: 'cover',
          boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
          marginBottom: 20,
        }}
      />
<F1Button link="https://example.com/events">Events</F1Button>
<F1Button link="https://instagram.com/yourhandle">Instagram</F1Button>
<F1Button link="https://example.com/join">Become a Member</F1Button>
<F1Button link="https://tiktok.com/@yourprofile">TikTok</F1Button>

    </div>
  );
};

export default App;
