import React, { useState, useEffect } from 'react';

const F1Button = ({ children, onClick }) => {
  const [isHover, setHover] = useState(false);

  const buttonStyle = {
    display: 'inline-block',
    background: isHover
    ? 'linear-gradient(90deg, #ec1c64, #a60032)'
    : 'linear-gradient(90deg, #a60032, #ec1c64)',
    color: '#fff',
    fontWeight: 900,
    fontSize: 20,
    padding: '14px 28px',
    textAlign: 'center',
    textTransform: 'uppercase',
    border: 'none',
    cursor: 'pointer',
    position: 'relative',
    margin: '16px auto',
    width: '80%',
    maxWidth: '400px',
    transition: 'all 0.3s ease',
    clipPath: 'polygon(5% 0%, 100% 0%, 100% 85%, 97% 100%, 0% 100%)',
    borderBottomRightRadius: 30,
    //boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
    transform: isHover ? 'translateY(-2px)' : 'translateY(0)',
  };

  return (
    <button
      onClick={onClick}
      onMouseEnter={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
      style={buttonStyle}
    >
      {children}
    </button>
  );
};

const App = () => {
  const [showLights, setShowLights] = useState(false);
  const [lightsOn, setLightsOn] = useState(0);
  const [destination, setDestination] = useState('');

  const handleLaunch = (url) => {
    setDestination(url);
    setShowLights(true);
    setLightsOn(0);
  };

  useEffect(() => {
    if (showLights && lightsOn < 5) {
      const interval = setTimeout(() => {
        setLightsOn((prev) => prev + 1);
      }, 300); // Time between each light
      return () => clearTimeout(interval);
    }

    if (lightsOn === 5) {
      // Wait briefly before redirecting
      const launch = setTimeout(() => {
        window.location.href = destination;
      }, 300);
      return () => clearTimeout(launch);
    }
  }, [showLights, lightsOn, destination]);

  return (
    <>
        <style>
        {`
            @keyframes fadeInOverlay {
            from { opacity: 0; }
            to { opacity: 1; }
            }
        `}
        </style>

      {/* F1 Light Start Animation */}
    {showLights && (
    <div
        style={{
        position: 'fixed',
        inset: 0,
        backdropFilter: 'blur(8px)',
        backgroundColor: 'rgba(0,0,0,0.4)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999,
        opacity: 1,
        animation: 'fadeInOverlay 0.3s ease forwards',
        }}
    >

          <div style={{ display: 'flex', gap: '20px' }}>
            {[0, 1, 2, 3, 4].map((i) => (
              <div
                key={i}
                style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  backgroundColor: i < lightsOn ? 'red' : '#222',
                  boxShadow: i < lightsOn ? '0 0 12px red' : 'inset 0 0 4px #111',
                  transition: 'background-color 0.2s ease',
                }}
              ></div>
            ))}
          </div>
        </div>
      )}

      {/* Main Page */}
      <div
        style={{
          position: 'absolute',
          top: 'env(safe-area-inset-top, 0)',
          left: 0,
          right: 0,
          bottom: 'env(safe-area-inset-bottom, 0)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          padding: '20px',
          backgroundImage: 'url("/ntu_f1_car_image.png")',
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
          backgroundColor: '#000',
          overflow: 'hidden',
          textAlign: 'center',
        }}
      >
        <img
          src="/image_1.png"
          alt="NTU F1 Logo"
          style={{
            width: 120,
            height: 120,
            borderRadius: '50%',
            objectFit: 'cover',
            boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
            marginBottom: 20,
          }}
        />
        <F1Button onClick={() => handleLaunch('https://example.com/events')}>Events</F1Button>
        <F1Button onClick={() => handleLaunch('https://instagram.com/yourhandle')}>Instagram</F1Button>
        <F1Button onClick={() => handleLaunch('https://example.com/join')}>Become a Member</F1Button>
        <F1Button onClick={() => handleLaunch('https://tiktok.com/@yourprofile')}>TikTok</F1Button>
      </div>
    </>
  );
};

export default App;
